mapper.update:
update employee  set 
empname =#{empname}, empbirth=#{empbirth}, emptel=#{emptel}, empaddr=#{empaddr}, empauth=#{empauth}, empid=#{empid}, emppass=password(#{emppass}), empretired=#{empretired}
where empno=#{empno}

sql.where:
    empretired=#{empretired}


		 and empno like CONCAT('%','${keyword}','%')


		 and empname like CONCAT('%','${keyword}','%')
